def threat_sources
  ThreatSource.create(category: 'Adversarial', description: 'Individuals, groups, organizations, or states that seek to exploit the organization’s dependence on cyber resources (i.e., information in electronic form, information and communications technologies, and the communications and information-handling capabilities provided by those technologies).', is_adversarial: true)
  ThreatSource.create(category: 'Accidental', description: 'Erroneous actions taken by individuals in the course of executing their everyday responsibilities.', is_adversarial: false)
  ThreatSource.create(category: 'Structural', description: 'Failures of equipment, environmental controls, or software due to aging, resource depletion, or other circumstances which exceed expected operating parameters', is_adversarial: false)
  ThreatSource.create(category: 'Environmental', description: 'Natural disasters and failures of critical infrastructures on which the organization depends, but which are outside the control of the organization.
  Note: Natural and man-made disasters can also be characterized in terms of their severity and/or duration. However, because the threat source and the threat event are strongly identified, severity and duration can be included in the description of the threat event (e.g., Category 5 hurricane causes extensive damage to the facilities housing mission-critical systems, making those systems unavailable for three weeks).', is_adversarial: false)
end

def adversarial_threat_source_capabilities
  AdversarialThreatSourceCapability.create(qualitative: "Very High", quantitative: 10, description: "The adversary has a very sophisticated level of expertise, is well-resourced, and can generate opportunities to support multiple successful, continuous, and coordinated attacks.")
  AdversarialThreatSourceCapability.create(qualitative: "High", quantitative: 8, description: "The adversary has a sophisticated level of expertise, with significant resources and opportunities to support multiple successful coordinated attacks.")
  AdversarialThreatSourceCapability.create(qualitative: "Moderate", quantitative: 5, description: "The adversary has moderate resources, expertise, and opportunities to support multiple successful attacks.")
  AdversarialThreatSourceCapability.create(qualitative: "Low", quantitative: 2, description: "The adversary has limited resources, expertise, and opportunities to support a successful attack.")
  AdversarialThreatSourceCapability.create(qualitative: "Very Low", quantitative: 0, description: "The adversary has very limited resources, expertise, and opportunities to support a successful attack.")
end

def adversarial_threat_source_intents
  AdversarialThreatSourceIntent.create(qualitative: "Very High", quantitative: 10, description: "The adversary seeks to undermine, severely impede, or destroy a core mission or business function, program, or enterprise by exploiting a presence in the organization’s information systems or infrastructure. The adversary is concerned about disclosure of tradecraft only to the extent that it would impede its ability to complete stated goals.")
  AdversarialThreatSourceIntent.create(qualitative: "High", quantitative: 8, description: "The adversary seeks to undermine/impede critical aspects of a core mission or business function, program, or enterprise, or place itself in a position to do so in the future, by maintaining a presence in the organization’s information systems or infrastructure. The adversary is very concerned about minimizing attack detection/disclosure of tradecraft, particularly while preparing for future attacks.")
  AdversarialThreatSourceIntent.create(qualitative: "Moderate", quantitative: 5, description: "The adversary seeks to obtain or modify specific critical or sensitive information or usurp/disrupt the organization’s cyber resources by establishing a foothold in the organization’s information systems or infrastructure. The adversary is concerned about minimizing attack detection/disclosure of tradecraft, particularly when carrying out attacks over long time periods. The adversary is willing to impede aspects of the organization’s missions/business functions to achieve these ends.")
  AdversarialThreatSourceIntent.create(qualitative: "Low", quantitative: 2, description: "The adversary actively seeks to obtain critical or sensitive information or to usurp/disrupt the organization’s cyber resources, and does so without concern about attack detection/disclosure of tradecraft.")
  AdversarialThreatSourceIntent.create(qualitative: "Very Low", quantitative: 0, description: "The adversary seeks to usurp, disrupt, or deface the organization’s cyber resources, and does so without concern about attack detection/disclosure of tradecraft.")
end

def adversarial_threat_source_targetings
  AdversarialThreatSourceTargeting.create(qualitative: "Very High", quantitative: 10, description: "The adversary analyzes information obtained via reconnaissance and attacks to target persistently a specific organization, enterprise, program, mission or business function, focusing on specific high-value or mission-critical information, resources, supply flows, or functions; specific employees or positions; supporting infrastructure providers/suppliers; or partnering organizations.")
  AdversarialThreatSourceTargeting.create(qualitative: "High", quantitative: 8, description: "The adversary analyzes information obtained via reconnaissance to target persistently a specific organization, enterprise, program, mission or business function, focusing on specific high-value or mission-critical information, resources, supply flows, or functions, specific employees supporting those functions, or key positions.")
  AdversarialThreatSourceTargeting.create(qualitative: "Moderate", quantitative: 5, description: "The adversary analyzes publicly available information to target persistently specific high-value organizations (and key positions, such as Chief Information Officer), programs, or information.")
  AdversarialThreatSourceTargeting.create(qualitative: "Low", quantitative: 2, description: "The adversary uses publicly available information to target a class of high-value organizations or information, and seeks targets of opportunity within that class.")
  AdversarialThreatSourceTargeting.create(qualitative: "Very Low", quantitative: 0, description: "The adversary may or may not target any specific organizations or classes of organizations.")
end

def non_adversarial_threat_source_effects
  NonAdversarialThreatSourceEffect.create(qualitative: "Very High", quantitative: 10, description: "The effects of the error, accident, or act of nature are sweeping, involving almost all of the cyber resources of the [Tier 3: information systems; Tier 2: mission/business processes or EA segments, common infrastructure, or support services; Tier 1: organization/governance structure].")
  NonAdversarialThreatSourceEffect.create(qualitative: "High", quantitative: 8, description: "The effects of the error, accident, or act of nature are extensive, involving most of the cyber resources of the [Tier 3: information systems; Tier 2: mission/business processes or EA segments, common infrastructure, or support services; Tier 1: organization/governance structure], including many critical resources.")
  NonAdversarialThreatSourceEffect.create(qualitative: "Moderate", quantitative: 5, description: "The effects of the error, accident, or act of nature are wide-ranging, involving a significant portion of the cyber resources of the [Tier 3: information systems; Tier 2: mission/business processes or EA segments, common infrastructure, or support services; Tier 1: organization/governance structure], including some critical resources.")
  NonAdversarialThreatSourceEffect.create(qualitative: "Low", quantitative: 2, description: "The effects of the error, accident, or act of nature are limited, involving some of the cyber resources of the [Tier 3: information systems; Tier 2: mission/business processes or EA segments, common infrastructure, or support services; Tier 1: organization/governance structure], but involving no critical resources.")
  NonAdversarialThreatSourceEffect.create(qualitative: "Very Low", quantitative: 0, description: "The effects of the error, accident, or act of nature are minimal, involving few if any of the cyber resources of the [Tier 3: information systems; Tier 2: mission/business processes or EA segments, common infrastructure, or support services; Tier 1: organization/governance structure], and involving no critical resources.")
end

def threat_event_relevances
  ThreatEventRelevance.create(qualitative: "Confirmed", description: "The threat event or TTP has been seen by the organization.")
  ThreatEventRelevance.create(qualitative: "Expected", description: "The threat event or TTP has been seen by the organization’s peers or partners.")
  ThreatEventRelevance.create(qualitative: "Anticipated", description: "The threat event or TTP has been reported by a trusted source.")
  ThreatEventRelevance.create(qualitative: "Predicted", description: "The threat event or TTP has been predicted by a trusted source.")
  ThreatEventRelevance.create(qualitative: "Possible", description: "The threat event or TTP has been described by a somewhat credible source.")
  ThreatEventRelevance.create(qualitative: "N/A", description: "The threat event or TTP is not currently applicable. For example, a threat event or TTP could assume specific technologies, architectures, or processes that are not present in the organization, mission/business process, EA segment, or information system; or predisposing conditions that are not present (e.g., location in a flood plain). Alternately, if the organization is using detailed or specific threat information, a threat event or TTP could be deemed inapplicable because information indicates that no adversary is expected to initiate the threat event or use the TTP.")
end

def vulnerability_severities
  VulnerabilitySeverity.create(qualitative: "Very High", quantitative: 10, description: "The vulnerability is exposed and exploitable, and its exploitation could result in severe impacts.
Relevant security control or other remediation is not implemented and not planned; or no security measure can be identified to remediate the vulnerability.")
  VulnerabilitySeverity.create(qualitative: "High", quantitative: 8, description: "The vulnerability is of high concern, based on the exposure of the vulnerability and ease of exploitation and/or on the severity of impacts that could result from its exploitation.
Relevant security control or other remediation is planned but not implemented; compensating controls are in place and at least minimally effective.")
  VulnerabilitySeverity.create(qualitative: "Moderate", quantitative: 5, description: "The vulnerability is of moderate concern, based on the exposure of the vulnerability and ease of exploitation and/or on the severity of impacts that could result from its exploitation.
Relevant security control or other remediation is partially implemented and somewhat effective.")
  VulnerabilitySeverity.create(qualitative: "Low", quantitative: 2, description: "The vulnerability is of minor concern, but effectiveness of remediation could be improved.
Relevant security control or other remediation is fully implemented and somewhat effective.")
  VulnerabilitySeverity.create(qualitative: "Very Low", quantitative: 0, description: "The vulnerability is not of concern.
Relevant security control or other remediation is fully implemented, assessed, and effective.")
end

def predisposing_conditions
  PredisposingCondition.create(category: "Information-Related", description: "Needs to handle information (as it is created, transmitted, stored, processed, and/or displayed) in a specific manner, due to its sensitivity (or lack of sensitivity), legal or regulatory requirements, and/or contractual or other organizational agreements.")
  PredisposingCondition.create(category: "Technical", description: "Needs to use technologies in specific ways.")
  PredisposingCondition.create(category: "Operational / Environmental", description: "Ability to rely upon physical, procedural, and personnel controls provided by the operational environment.")
end

def predisposing_condition_pervasivenesses
  PredisposingConditionPervasiveness.create(qualitative: "Very High", quantitative: 10, description: "Applies to all organizational missions/business functions (Tier 1), mission/business processes (Tier 2), or information systems (Tier 3).")
  PredisposingConditionPervasiveness.create(qualitative: "High", quantitative: 8, description: "Applies to most organizational missions/business functions (Tier 1), mission/business processes (Tier 2), or information systems (Tier 3).")
  PredisposingConditionPervasiveness.create(qualitative: "Moderate", quantitative: 5, description: "Applies to many organizational missions/business functions (Tier 1), mission/business processes (Tier 2), or information systems (Tier 3).")
  PredisposingConditionPervasiveness.create(qualitative: "Low", quantitative: 2, description: "Applies to some organizational missions/business functions (Tier 1), mission/business processes (Tier 2), or information systems (Tier 3).")
  PredisposingConditionPervasiveness.create(qualitative: "Very Low", quantitative: 0, description: "Applies to few organizational missions/business functions (Tier 1), mission/business processes (Tier 2), or information systems (Tier 3).")
end

def threat_event_initiation_likelihoods
  ThreatEventInitiationLikelihood.create(qualitative: "Very High", quantitative: 10, description: "Adversary is almost certain to initiate the threat event.")
  ThreatEventInitiationLikelihood.create(qualitative: "High", quantitative: 8, description: "Adversary is highly likely to initiate the threat event.")
  ThreatEventInitiationLikelihood.create(qualitative: "Moderate", quantitative: 5, description: "Adversary is somewhat likely to initiate the treat event.")
  ThreatEventInitiationLikelihood.create(qualitative: "Low", quantitative: 2, description: "Adversary is unlikely to initiate the threat event.")
  ThreatEventInitiationLikelihood.create(qualitative: "Very Low", quantitative: 0, description: "Adversary is highly unlikely to initiate the threat event.")
end

def threat_event_occurence_likelihoods
  ThreatEventOccurenceLikelihood.create(qualitative: "Very High", quantitative: 10, description: "Error, accident, or act of nature is almost certain to occur; or occurs more than 100 times a year.")
  ThreatEventOccurenceLikelihood.create(qualitative: "High", quantitative: 8, description: "Error, accident, or act of nature is highly likely to occur; or occurs between 10-100 times a year.")
  ThreatEventOccurenceLikelihood.create(qualitative: "Moderate", quantitative: 5, description: "Error, accident, or act of nature is somewhat likely to occur; or occurs between 1-10 times a year.")
  ThreatEventOccurenceLikelihood.create(qualitative: "Low", quantitative: 2, description: "Error, accident, or act of nature is unlikely to occur; or occurs less than once a year, but more than once every 10 years.")
  ThreatEventOccurenceLikelihood.create(qualitative: "Very Low", quantitative: 0, description: "Error, accident, or act of nature is highly unlikely to occur; or occurs less than once every 10 years.")
end

def threat_event_adverse_impact_likelihoods
  ThreatEventAdverseImpactLikelihood.create(qualitative: "Very High", quantitative: 10, description: "If the threat event is initiated or occurs, it is almost certain to have adverse impacts.")
  ThreatEventAdverseImpactLikelihood.create(qualitative: "High", quantitative: 8, description: "If the threat event is initiated or occurs, it is highly likely to have adverse impacts.")
  ThreatEventAdverseImpactLikelihood.create(qualitative: "Moderate", quantitative: 5, description: "If the threat event is initiated or occurs, it is somewhat likely to have adverse impacts.")
  ThreatEventAdverseImpactLikelihood.create(qualitative: "Low", quantitative: 2, description: "If the threat event is initiated or occurs, it is unlikely to have adverse impacts.")
  ThreatEventAdverseImpactLikelihood.create(qualitative: "Very Low", quantitative: 0, description: "If the threat event is initiated or occurs, it is highly unlikely to have adverse impacts.")
end

